DROP TABLE mac;
CREATE TABLE mac (
  id NUMBER GENERATED BY DEFAULT AS IDENTITY NOT NULL,
  bedno VARCHAR2(255),
  status NUMBER(1) DEFAULT 1,
  arrive TIMESTAMP,
  name VARCHAR2(255),
  urn VARCHAR2(255),
  gp VARCHAR2(255),
  gestation VARCHAR2(255),
  assess VARCHAR2(255),
  induction NUMBER,
  risk VARCHAR2(255),
  pathway VARCHAR2(255),
  rom VARCHAR2(255),
  bldgrp VARCHAR2(255),
  gbs VARCHAR2(255),
  sero VARCHAR2(255),
  epi VARCHAR2(255),
  progress VARCHAR2(255),
  mx VARCHAR2(255),
  mw VARCHAR2(255),
  loc VARCHAR2(255),
  breq TIMESTAMP,
  balloc VARCHAR2(255),
  PRIMARY KEY (id)
);

